<?php/* Set e-mail recipient */$myemail = "hgl@ecomp.poli.br";/* Check all form inputs using check_input function */$yourname = check_input($_POST['yourname'], "Enter your name");$email = check_input($_POST['email']);$phone = check_input($_POST['phone']);$msg = check_input($_POST['msg']);$subject = "";/* If e-mail is not valid show error message */if (!preg_match("/([\w\-]+\@[\w\-]+\.[\w\-]+)/", $email)){ echo "<script> alert('Digite um email válido'); window.location = 'contato.php'; </script>";}/* Let's prepare the message for the e-mail */$message = "Olá!Alguem se inscreveu no formulario de contato do seu site:Nome: $yournameTelefone: $phoneE-mail: $emailMensagem: $msgFim da mensagem";/* Send the message using mail() function */mail($myemail, $subject, $message);/* Redirect visitor to the thank you page */echo "<script> alert('Sua mensagem foi enviada com sucesso'); window.location = 'contato.php'; </script>";/* Functions we used */function check_input($data, $problem=''){ $data = trim($data); $data = stripslashes($data); $data = htmlspecialchars($data); if ($problem && strlen($data) == 0) { show_error($problem); } return $data;}?>